steps.command:
echo 'ðŸ” SLACK INTEGRATION DEBUG'
echo '========================'
echo 'SLACK_TOKEN: '${slack_token}
echo 'NORMALIZED_CHANNEL: '${NORMALIZED_CHANNEL_NAME}
echo 'ORIGINAL_CHANNEL: '${slack_channel_id}
echo 'INCIDENT_ID: '${incident_id}
echo 'INCIDENT_TITLE: '${incident_title}
echo 'ENVIRONMENT: '${environment}
echo ''
echo 'TOKEN VALIDATION:'
if [ -z "${slack_token}" ]; then echo 'âŒ TOKEN IS EMPTY'; else echo 'âœ… TOKEN EXISTS'; fi
if [ "${slack_token}" = "null" ]; then echo 'âŒ TOKEN IS NULL'; else echo 'âœ… TOKEN IS NOT NULL'; fi
if [ -z "${NORMALIZED_CHANNEL_NAME}" ]; then echo 'âŒ CHANNEL NAME IS EMPTY'; else echo 'âœ… CHANNEL NAME EXISTS'; fi
echo ''
echo 'READY FOR SLACK POSTING'

echo 'ðŸš¨ POSTING INCIDENT ALERT'
echo '========================'

# Check if we have a Slack token
if [ -z "${slack_token}" ] || [ "${slack_token}" = "null" ]; then
  echo 'âš ï¸ No Slack token available - skipping Slack alert'
  echo 'âœ… Incident alert skipped (no Slack integration)'
  exit 0
fi

# Normalize channel name
CHANNEL_NORMALIZED=$(echo "${slack_channel_id}" | tr '[:upper:]' '[:lower:]' | sed 's/ /_/g' | sed 's/[^a-z0-9#_-]/_/g' | sed 's/_\+/_/g')

echo "Original channel: ${slack_channel_id}"
echo "Normalized channel: ${CHANNEL_NORMALIZED}"

# Set severity emoji
SEVERITY_EMOJI=''
case "${incident_severity}" in
  critical) SEVERITY_EMOJI='ðŸ”´' ;;
  high) SEVERITY_EMOJI='ðŸŸ ' ;;
  medium) SEVERITY_EMOJI='ðŸŸ¡' ;;
  low) SEVERITY_EMOJI='ðŸŸ¢' ;;
  *) SEVERITY_EMOJI='âšª' ;;
esac

echo "Using severity emoji: ${SEVERITY_EMOJI} for ${incident_severity}"

# Create JSON payload using heredoc to avoid escaping issues
cat > /tmp/slack_payload.json << EOF
{
  "channel": "${CHANNEL_NORMALIZED}",
  "text": "ðŸš¨ INCIDENT: ${incident_title}",
  "blocks": [
    {
      "type": "header",
      "text": {
        "type": "plain_text",
        "text": "ðŸš¨ PRODUCTION INCIDENT ALERT",
        "emoji": true
      }
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": "*${incident_title}*"
      }
    },
    {
      "type": "section",
      "fields": [
        {
          "type": "mrkdwn",
          "text": "*ID:* ${incident_id}"
        },
        {
          "type": "mrkdwn",
          "text": "*Severity:* ${SEVERITY_EMOJI} ${incident_severity}"
        }
      ]
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": "${incident_body}"
      }
    },
    {
      "type": "actions",
      "elements": [
        {
          "type": "button",
          "text": {
            "type": "plain_text",
            "text": "ðŸ“Š View on Datadog",
            "emoji": true
          },
          "url": "${incident_url}",
          "style": "primary"
        }
      ]
    }
  ]
}
EOF

echo 'Posting alert to Slack...'
RESPONSE=$(curl -s -X POST https://slack.com/api/chat.postMessage \
  -H "Authorization: Bearer ${slack_token.token}" \
  -H "Content-Type: application/json" \
  -d @/tmp/slack_payload.json)

echo "Slack API Response: ${RESPONSE}"

if echo "${RESPONSE}" | grep -q '"ok":true'; then
  echo 'âœ… Incident alert posted successfully to Slack!'
else
  echo 'âš ï¸ Failed to post incident alert to Slack - continuing workflow'
  echo "Response: ${RESPONSE}"
fi

echo 'âœ… Alert step completed'

echo 'ðŸ“‹ POSTING INVESTIGATION SUMMARY TO SLACK'
echo '======================================='

# Check if we have a Slack token
if [ -z "${slack_token}" ] || [ "${slack_token}" = "null" ]; then
  echo 'âš ï¸ No Slack token available - skipping summary post'
  echo 'âœ… Summary post skipped (no Slack integration)'
  exit 0
fi

# Normalize channel name same as initial alert
CHANNEL_NORMALIZED=$(echo "${slack_channel_id}" | tr '[:upper:]' '[:lower:]' | sed 's/ /_/g' | sed 's/[^a-z0-9#_-]/_/g' | sed 's/_\+/_/g')

