steps.command:
echo 'DEBUG: Slack Integration Status'
echo 'SLACK_TOKEN VALUE:' ${slack_token}
echo 'SLACK_TOKEN LENGTH:' ${#slack_token}
echo 'SLACK_TOKEN TYPE:' $(echo ${slack_token} | head -c 20)
echo 'NORMALIZED_CHANNEL:' ${NORMALIZED_CHANNEL_NAME}
if [ -z "${slack_token}" ]; then echo 'TOKEN IS EMPTY'; else echo 'TOKEN EXISTS'; fi
if [ "${slack_token}" = "null" ]; then echo 'TOKEN IS NULL'; else echo 'TOKEN IS NOT NULL'; fi

echo '📋 POSTING INVESTIGATION SUMMARY TO SLACK' && echo '=======================================' && if [ -z '${slack_token}' ] || [ '${slack_token}' = 'null' ]; then echo '⚠️ No Slack token available - skipping summary post' && echo '✅ Summary post skipped (no Slack integration)' && exit 0; fi && SEVERITY_EMOJI='' && case '${incident_severity}' in critical) SEVERITY_EMOJI='🔴' ;; high) SEVERITY_EMOJI='🟠' ;; medium) SEVERITY_EMOJI='🟡' ;; low) SEVERITY_EMOJI='🟢' ;; *) SEVERITY_EMOJI='⚪' ;; esac && cat > /tmp/slack_payload.json << EOF
{
  "channel": "${NORMALIZED_CHANNEL_NAME}",
  "text": "✅ AI Investigation Complete",
  "blocks": [
    {
      "type": "header",
      "text": {
        "type": "plain_text",
        "text": "🚨 INCIDENT INVESTIGATION COMPLETE",
        "emoji": true
      }
    },
    {
      "type": "section",
      "fields": [
        {
          "type": "mrkdwn",
          "text": "*Alert ID:* ${alert_id}"
        },
        {
          "type": "mrkdwn",
          "text": "*Status:* ${alert_status}"
        },
        {
          "type": "mrkdwn",
          "text": "*Priority:* ${alert_priority}"
        },
        {
          "type": "mrkdwn",
          "text": "*Environment:* ${environment}"
        }
      ]
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": "*🔍 Investigation Summary:*\n${executive_summary}"
      }
    },
    {
      "type": "divider"
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": "*📊 Full Investigation Results:*\n${cluster_results}"
      }
    },
    {
      "type": "divider"
    },
    {
      "type": "actions",
      "elements": [
        {
          "type": "button",
          "text": {
            "type": "plain_text",
            "text": "🔗 View Datadog Alert",
            "emoji": true
          },
          "url": "${incident_url}",
          "style": "primary"
        },
        {
          "type": "button",
          "text": {
            "type": "plain_text",
            "text": "📋 Follow Up Investigation",
            "emoji": true
          },
          "value": "investigate_further",
          "action_id": "investigate_further"
        }
      ]
    }
  ]
}
EOF
echo 'Posting enhanced summary to Slack...' && curl -s -X POST https://slack.com/api/chat.postMessage -H 'Authorization: Bearer ${slack_token}' -H 'Content-Type: application/json' -d @/tmp/slack_payload.json && echo '✅ Investigation summary posted successfully to Slack!'

