steps.command:
echo 'üîç SLACK INTEGRATION DEBUG'
echo '========================'
echo 'SLACK_TOKEN: '${slack_token}
echo 'NORMALIZED_CHANNEL: '${NORMALIZED_CHANNEL_NAME}
echo 'ORIGINAL_CHANNEL: '${slack_channel_id}
echo 'INCIDENT_ID: '${incident_id}
echo 'INCIDENT_TITLE: '${incident_title}
echo 'ENVIRONMENT: '${environment}
echo ''
echo 'TOKEN VALIDATION:'
if [ -z "${slack_token}" ]; then echo '‚ùå TOKEN IS EMPTY'; else echo '‚úÖ TOKEN EXISTS'; fi
if [ "${slack_token}" = "null" ]; then echo '‚ùå TOKEN IS NULL'; else echo '‚úÖ TOKEN IS NOT NULL'; fi
if [ -z "${NORMALIZED_CHANNEL_NAME}" ]; then echo '‚ùå CHANNEL NAME IS EMPTY'; else echo '‚úÖ CHANNEL NAME EXISTS'; fi
echo ''
echo 'READY FOR SLACK POSTING'

echo 'üìã POSTING INVESTIGATION SUMMARY TO SLACK' && echo '=======================================' && if [ -z '${slack_token}' ] || [ '${slack_token}' = 'null' ]; then echo '‚ö†Ô∏è No Slack token available - skipping summary post' && echo '‚úÖ Summary post skipped (no Slack integration)' && exit 0; fi && SEVERITY_EMOJI='' && case '${incident_severity}' in critical) SEVERITY_EMOJI='üî¥' ;; high) SEVERITY_EMOJI='üü†' ;; medium) SEVERITY_EMOJI='üü°' ;; low) SEVERITY_EMOJI='üü¢' ;; *) SEVERITY_EMOJI='‚ö™' ;; esac && cat > /tmp/slack_payload.json << EOF
{
  "channel": "${NORMALIZED_CHANNEL_NAME}",
  "text": "‚úÖ AI Investigation Complete",
  "blocks": [
    {
      "type": "header",
      "text": {
        "type": "plain_text",
        "text": "üö® INCIDENT INVESTIGATION COMPLETE",
        "emoji": true
      }
    },
    {
      "type": "section",
      "fields": [
        {
          "type": "mrkdwn",
          "text": "*Alert ID:* ${alert_id}"
        },
        {
          "type": "mrkdwn",
          "text": "*Status:* ${alert_status}"
        },
        {
          "type": "mrkdwn",
          "text": "*Agent Used:* ${classified_agent}"
        },
        {
          "type": "mrkdwn",
          "text": "*Environment:* ${environment}"
        }
      ]
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": "*üîç Investigation Summary:*\n${executive_summary}"
      }
    },
    {
      "type": "divider"
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": "*üìä Full Investigation Results:*\n${cluster_results}"
      }
    },
    {
      "type": "divider"
    },
    {
      "type": "actions",
      "elements": [
        {
          "type": "button",
          "text": {
            "type": "plain_text",
            "text": "üîó View Datadog Alert",
            "emoji": true
          },
          "url": "${incident_url}",
          "style": "primary"
        },
        {
          "type": "button",
          "text": {
            "type": "plain_text",
            "text": "üìã Follow Up Investigation",
            "emoji": true
          },
          "value": "investigate_further",
          "action_id": "investigate_further"
        }
      ]
    }
  ]
}
EOF
echo 'Posting enhanced summary to Slack...' && curl -s -X POST https://slack.com/api/chat.postMessage -H 'Authorization: Bearer ${slack_token}' -H 'Content-Type: application/json' -d @/tmp/slack_payload.json && echo '‚úÖ Investigation summary posted successfully to Slack!'
